Scripts.OnEditFocusLost:
local value = this:GetText();
if not BCEPGP_isNumber(value) then
	BCEPGP_print("Base coefficient must contain numbers only", true);
	this:SetText(tostring(COEF));
elseif value == "" then
	BCEPGP_print("Base GP coefficient cannot be blank", true);
	this:SetText(tostring(COEF));
elseif tonumber(this:GetText()) == 0 then
	BCEPGP_print("Base GP coefficient cannot be 0", true);
	this:SetText(tostring(COEF));
elseif COEF ~= tonumber(value)
	COEF = tonumber(value);
	BCEPGP_print("Updated base coefficient");
end

local value = this:GetText();
if not BCEPGP_isNumber(value) then
	BCEPGP_print("GP modifier must contain numbers only", true);
	this:SetText(tostring(MOD));
elseif value == "" then
	BCEPGP_print("GP modifier cannot be blank", true);
	this:SetText(tostring(MOD));
elseif 0 >= tonumber(this:GetText()) then
	BCEPGP_print("GP modifier cannot be 0", true);
	this:SetText(tostring(MOD));
elseif MOD ~= tonumber(value) then
	MOD = tonumber(value);
	BCEPGP_print("Updated GP modifier");
end

local value = this:GetText();
if not BCEPGP_isNumber(value) then
	BCEPGP_print("Base GP must contain numbers only", true);
	this:SetText(tostring(BASEGP));
elseif value == "" then
	BCEPGP_print("Base GP cannot be blank", true);
	this:SetText(tostring(BASEGP));
elseif tonumber(this:GetText()) == 0 then
	BCEPGP_print("Base GP cannot be 0", true);
	this:SetText(tostring(BASEGP));
elseif 0 > tonumber(this:GetText()) then
	BCEPGP_print("Base GP must be a positive number", true);
	this:SetText(tostring(BASEGP));
elseif BASEGP ~= tonumber(value) then
	BASEGP = tonumber(value);
	BCEPGP_print("Updated base GP");
end
SetGuildRosterShowOffline(true);
BCEPGP_updateGuild();
if CanEditOfficerNote() == 1 then
	for i = 1, GetNumGuildMembers() do
		local name, rank, rankIndex, _, class, _, _, officerNote = GetGuildRosterInfo(i);
		if officerNote then
			local EP, GP = BCEPGP_getEPGP(officerNote);
			if BASEGP > GP then
				GuildRosterSetOfficerNote(i, EP .. "," .. BASEGP);
			end
		end
		if name then
			BCEPGP_roster[name] = {
			[1] = i,
			[2] = class,
			[3] = rank,
			[4] = rankIndex,
			[5] = officerNote
			};
		end
	end
end

