end

Scripts.OnEditFocusLost:
local value = this:GetText();
if not BCEPGP_isNumber(value) then
	if string.find(this:GetText(), "%%") then
		this:SetText(string.sub(value, 0, string.find(value, "%%")-1));
		STANDBYPERCENT = tonumber(value);
	else
		BCEPGP_print("Standby percent must contain numbers only", true);
		this:SetText(tostring(STANDBYPERCENT));
	end
else
	STANDBYPERCENT = tonumber(value);
end

