BCEPGP_groupVersion = {};
BCEPGP_vSearch = "GUILD";
BCEPGP_SendAddonMsg("version-check", "GUILD");
BCEPGP_UpdateVersionScrollBar();

if this:GetChecked() == 1 then
	BCEPGP_context_popup_GP_check:SetChecked(nil);
	BCEPGP_context_popup_desc:SetText("Adding EP");
else
	this:SetChecked(1);
end

if this:GetChecked() == 1 then
	BCEPGP_context_popup_EP_check:SetChecked(nil);
	BCEPGP_context_popup_desc:SetText("Adding GP");
else
	this:SetChecked(1);
end

PlaySound("gsTitleOptionExit");
local name = BCEPGP_guild_log_save_name:GetText();
BCEPGP_updateGuild();
if RECORDS[name] == nil or (BCEPGP_overwritelog and name == BCEPGP_recordholder) then
	if GetGuildRosterShowOffline() == nil then
		SetGuildRosterShowOffline(true);
		RECORDS[name] = {};
		SortGuildRoster(name);
		for i = 1, GetNumGuildMembers(), 1 do
			local _, _, _, _, _, _, _, oNote = GetGuildRosterInfo(i);
			RECORDS[name][GetGuildRosterInfo(i)] = oNote;
		end
		if BCEPGP_overwritelog then
			BCEPGP_print("Record overwritten [" .. name .. "]");
			BCEPGP_overwritelog = false;
		else
			BCEPGP_print("Record saved [" .. name .. "]");
		end
		SetGuildRosterShowOffline(false);
		HideUIPanel(BCEPGP_save_guild_logs);
	else
		RECORDS[name] = {};
		for i = 1, GetNumGuildMembers(), 1 do
			local _, _, _, _, _, _, _, oNote = GetGuildRosterInfo(i);
			RECORDS[name][GetGuildRosterInfo(i)] = oNote;
		end
		if BCEPGP_overwritelog and name == BCEPGP_recordholder then
			BCEPGP_print("Record overwritten [" .. name .. "]");
			BCEPGP_overwritelog = false;
		else
			BCEPGP_print("Record saved [" .. name .. "]");
		end
		HideUIPanel(BCEPGP_save_guild_logs);
	end
else
	BCEPGP_print("Record [" .. name .. "] already exists. Click confirm again to overwrite");
	BCEPGP_recordholder = name;
	BCEPGP_overwritelog = true;
end

