Scripts.OnClick:
local name = BCEPGP_guild_log_restore_name:GetText();
BCEPGP_updateGuild();
if RECORDS[name] ~= nil and BCEPGP_confirmrestore then
	if GetGuildRosterShowOffline() == nil then
		SetGuildRosterShowOffline(true);
		for i = 1, GetNumGuildMembers(), 1 do
			if RECORDS[name][GetGuildRosterInfo(i)] == nil then
				GuildRosterSetOfficerNote(i, "0,"..BASEGP);
			else
				GuildRosterSetOfficerNote(i, RECORDS[name][GetGuildRosterInfo(i)]);
			end
		end
		SetGuildRosterShowOffline(false);
		BCEPGP_print("Guild EPGP standings have been restored from record [" .. name .. "]");
		BCEPGP_confirmrestore = false;
		HideUIPanel(BCEPGP_restore_guild_logs);
	else
		for i = 1, GetNumGuildMembers(), 1 do
			if RECORDS[name][GetGuildRosterInfo(i)] == nil then
				GuildRosterSetOfficerNote(i, "0,"..BASEGP);
			else
				GuildRosterSetOfficerNote(i, RECORDS[name][GetGuildRosterInfo(i)]);
			end
		end
		BCEPGP_print("Guild EPGP standings have been restored from record [" .. name .. "]");
		BCEPGP_confirmrestore = false;
		HideUIPanel(BCEPGP_restore_guild_logs);
	end
elseif RECORDS[name] ~= nil then
	BCEPGP_print("Are you sure you would like to restore guild EPGP standings from record [" .. name .. "]?");
	BCEPGP_print("This action CANNOT be undone. Press confirm again to restore.");
	BCEPGP_confirmrestore = true;
else
	BCEPGP_print("Record [" .. name .. "] could not be found", true);
end

local BCEPGP_target = BCEPGP_settings_import_name:GetText();
if BCEPGP_raidRoster[BCEPGP_target] then
	BCEPGP_SendAddonMsg(BCEPGP_target .. "-import", "RAID");
elseif BCEPGP_roster[BCEPGP_target] then
	BCEPGP_SendAddonMsg(BCEPGP_target .. "-import", "GUILD");
else
	BCEPGP_print(BCEPGP_target .. " not found in guild or raid group", true);
end
BCEPGP_settings_import_name:ClearFocus();
HideUIPanel(BCEPGP_settings_import);

Scripts.OnEnterPressed:
this:ClearFocus();
PlaySound("gsTitleOptionExit");
if BCEPGP_override_item_name:GetText() == "" then
		BCEPGP_print("Enter an item name", true);
	return;
end
local item = BCEPGP_override_item_name:GetText();
local gp;
if BCEPGP_override_item_gp:GetText() == "" then
	gp = 0;
else
	gp = tonumber(BCEPGP_override_item_gp:GetText());
end
if (OVERRIDE_INDEX[item] and overwrite) or not OVERRIDE_INDEX[item] then
	OVERRIDE_INDEX[item] = gp;
	BCEPGP_print("GP value for " .. BCEPGP_override_item_name:GetText() .. " has been overriden to " .. gp);
	BCEPGP_UpdateOverrideScrollBar();
	BCEPGP_override_confirm = false;
	BCEPGP_override_item_name:SetText("");
	BCEPGP_override_item_gp:SetText("");
	BCEPGP_override_item_name:SetFocus();
else
	BCEPGP_print(BCEPGP_override_item_name:GetText() .. " has already been overridden to " .. OVERRIDE_INDEX[item] .. ". Click add again to change this to " .. gp);
	BCEPGP_override_confirm = true;
end

Scripts.OnClick:
PlaySound("gsTitleOptionExit");
if BCEPGP_override_item_name:GetText() == "" then
		BCEPGP_print("Enter an item name", true);
	return;
end
local item = BCEPGP_override_item_name:GetText();
local gp;
if BCEPGP_override_item_gp:GetText() == "" then
	gp = 0;
else
	gp = tonumber(BCEPGP_override_item_gp:GetText());
end
if (OVERRIDE_INDEX[item] and overwrite) or not OVERRIDE_INDEX[item] then
	OVERRIDE_INDEX[item] = gp;
	BCEPGP_print("GP value for " .. BCEPGP_override_item_name:GetText() .. " has been BCEPGP_overriden to " .. gp);
	BCEPGP_UpdateOverrideScrollBar();
	BCEPGP_override_confirm = false;
	BCEPGP_override_item_name:SetText("");
	BCEPGP_override_item_name:ClearFocus();
	BCEPGP_override_item_gp:SetText("");
	BCEPGP_override_item_gp:ClearFocus();
else
	BCEPGP_print(BCEPGP_override_item_name:GetText() .. " has already been overridden to " .. OVERRIDE_INDEX[item] .. ". Click add again to change this to " .. gp);
	BCEPGP_override_confirm = true;
end

Scripts.OnLoad:
this:RegisterForDrag("LeftButton");
BCEPGP_override_desc:SetText("This window allows your to permanently\n(or until the log is removed), override the gp\nvalue of an item to what ever you specify");
_, _, _, BCEPGP_pfUIFrames = GetAddOnInfo("pfUI");
if BCEPGP_pfUIFrames then
	BCEPGP_override_item_name:SetPoint("TOP", BCEPGP_override_item_name_string, "BOTTOM", 10, 0);
	BCEPGP_override_item_gp:SetPoint("TOP", BCEPGP_override_item_gp_string, "BOTTOM", 0, 0);
end

Scripts.OnClick:
PlaySound("gsTitleOptionExit");
if BCEPGP_traffic_clear then
	TRAFFIC = {};
	BCEPGP_UpdateTrafficScrollBar();
	BCEPGP_print("EPGP traffic wiped");
	BCEPGP_traffic_clear = false;
else
	BCEPGP_print("You are trying to clear your EPGP traffic log. This action cannot be reversed.");
	BCEPGP_print("Press Clear EPGP traffic again to confirm");
	BCEPGP_traffic_clear = true;
end

Scripts.OnLoad:
this:RegisterForDrag("LeftButton");
this:EnableKeyboard(true);
_, _, _, BCEPGP_pfUIFrames = GetAddOnInfo("pfUI");
if BCEPGP_pfUIFrames then
	for i = 1, 18 do
		getglobal("trafficButton" .. i .. "Action"):SetPoint("RIGHT", getglobal("trafficButton" .. i .. "Name"), "LEFT", 265, 0);
		getglobal("trafficButton" .. i .. "ItemName"):SetPoint("LEFT", getglobal("trafficButton" .. i .. "Name"), "LEFT", 280, 0);
		getglobal("trafficButton" .. i .. "EPBefore"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 530, 0);
		getglobal("trafficButton" .. i .. "EPAfter"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 570, 0);
		getglobal("trafficButton" .. i .. "GPBefore"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 640, 0);
		getglobal("trafficButton" .. i .. "GPAfter"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 680, 0);
	end
end
